package apierrors

import (
	"fmt"
	"net/http"

	"google.golang.org/grpc/codes"
	"google.golang.org/grpc/status"

	gwruntime "github.com/pensando/grpc-gateway/runtime"

	"github.com/pensando/sw/api"
	"github.com/pensando/sw/venice/utils/log"
	"github.com/pensando/sw/venice/utils/runtime"
)

// FromError extracts a api.Status object from error. This is possible for errors generated by
//  the api server. The api server attaches auxillary data to grpc errors which this utiility
//  function extracts
func FromError(err error) api.Status {
	if err == nil {
		return api.Status{}
	}
	ret := api.Status{Code: int32(gwruntime.HTTPStatusFromCode(codes.Unknown)), Message: []string{err.Error()}}
	grpcstatus, ok := status.FromError(err)
	if !ok {
		return ret
	}

	details := grpcstatus.Details()
	for _, d := range details {
		// we expect only one status object.
		if _, ok := d.(*api.Status); ok {
			ret = *d.(*api.Status)
			break
		}
	}
	ret.Code = int32(HTTPStatusFromCode(grpcstatus.Code()))
	return ret
}

// ToGrpcError generates an error to be used as return for gRPC errors on the server. This utility
//  function encodes an api.Status message to a grpc error which can then be extracted by the
//  grpc client
func ToGrpcError(in interface{}, msg []string, code int32, uri string, obj runtime.Object) error {
	ref := &api.ObjectRef{}
	if obj != nil {
		ref.Kind = obj.GetObjectKind()
		if meta, ok := obj.(runtime.ObjectMetaAccessor); ok {
			objmeta := meta.GetObjectMeta()
			ref.Name = objmeta.Name
			ref.Tenant = objmeta.Tenant
			ref.Namespace = objmeta.Namespace
		}
	}
	ref.URI = uri
	apistatus := api.Status{
		TypeMeta: api.TypeMeta{Kind: "Status"},
		Code:     code,
		Result:   api.StatusResult{Str: fmt.Sprintf("%v", in)},
		Message:  msg,
		Ref:      ref,
	}
	grpcstatus := status.New(codes.Code(code), fmt.Sprintf("%v", in))
	grpcstatus, e := grpcstatus.WithDetails(&apistatus)
	if e != nil {
		log.Errorf("failed to create grpcStatus (%s)", e)
		return fmt.Errorf("%v", in)
	}
	return grpcstatus.Err()
}

// AddDetails creates a grpc error given an api.Status
func AddDetails(s *api.Status) error {
	grpcstatus := status.New(codes.Code(s.Code), s.Result.Str)
	grpcstatus, err := grpcstatus.WithDetails(s)
	if err != nil {
		log.Errorf("failed to create grpcStatus (%s)", err)
		return s
	}
	return grpcstatus.Err()
}

// HTTPStatusFromCode converts a gRPC error code into the corresponding HTTP response status.
func HTTPStatusFromCode(code codes.Code) int {
	switch code {
	case codes.ResourceExhausted:
		return http.StatusRequestEntityTooLarge
	default:
		return gwruntime.HTTPStatusFromCode(code)
	}
}
